import React, { useEffect, useState } from "react";
import { Header } from "./Header";
import { SideNav } from "./templates/SideNav";
import { Footer } from "./Footer";
import { useNavigate, useParams } from "react-router-dom";
import JobListingService from "../services/JobListingService";

export const JobProfile = () => {
  const [requirements, setRequirements] = useState("");
  const [description, setDescription] = useState("");
  const [title, setTitle] = useState("");
  const [image, setImage] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();
  useEffect(() => {
    console.log("useEffect triggered.... ");
    console.log("id value obtained from url using useParams()", id);
    if (id) {
      JobListingService.getJobListingById(id)
        .then((response) => {
          console.log(
            "Response recieved from getbyid API",
            response.data
          );
          setTitle(response.data.title);
          setDescription(response.data.description);
          setRequirements(response.data.requirements);
          setImage(response.data.image)
          console.log("state variable changed. ");
        })
        .catch((error) => {
          console.log("Error recieved from save api...", error);
        });
    }
  }, []);
  return (
    <div className="w-[100%] h-full flex flex-col overflow-x-hidden">
      <Header />
      <div className="bg-gradient-to-r from-slate-900 via-red-900 to-slate-900 w-full h-full flex ">
        <SideNav className="w-[16%]" />
        <div className="w-[84%] h-[100%]">
          <div
            className={`w-full h-full flex flex-col items-center gap-4 overflow-auto `}
            style={{ scrollbarWidth: "none" }}
          >
            <img src={`${image}`} alt=""  className=" w-32 h-32 object-cover rounded-full mt-5"/>
            <div className=" text-zinc-800  p-2 bg-gradient-to-r from-rose-100 to-teal-100  -translate-y-5 text-xl font-bold rounded-2xl mt-2 ">{title}</div>
            <div className="text-zinc-100 h-30 text-center"></div>
            <div className="text-zinc-100 h-30 text-center">Requirements Lorem ipsum dolor sit amet consectetur, adipisicing elit. Quae ullam quisquam similique fugiat quibusdam provident molestiae porro minus iure inventore omnis aspernatur incidunt velit, praesentium eum tenetur assumenda quos natus.</div>
            <button className=" bg-black rounded-md text-base font-semibold text-zinc-100 p-2">Apply</button>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};
