import React from "react";
import { Link } from "react-router-dom";

export const SideNav = () => {
  return (
    <div className=" w-[20%] h-[100%] border-r-2 border-zinc-200 p-3 ">
      <h1 className="text-xl text-zinc-100 ">
        <i className="ri-profile-line "></i>
        <span className="ml-2">CareerCrafter</span>
      </h1>
      <nav className="text-zinc-100 flex flex-col">
        <h1 className=" text-base text-zinc-100 mt-2 mb-3">
         Jobs
        </h1>
        <Link to="" className="text-zinc-100 hover:bg-zinc-100 hover:text-black duration-200 rounded-full p-2 " style={{textDecoration: 'none'}}>By Location</Link>
        <Link className="text-zinc-100 hover:bg-zinc-100 hover:text-black duration-200 rounded-full p-2 ">By Description</Link>
        <Link className="text-zinc-100 hover:bg-zinc-100 hover:text-black duration-200 rounded-full p-2 ">By Title</Link>
        <Link className="text-zinc-100 hover:bg-zinc-100 hover:text-black duration-200 rounded-full p-2 ">By Requirements</Link>
        <Link className="text-zinc-100 hover:bg-zinc-100 hover:text-black duration-200 rounded-full p-2 ">By Company</Link>
      </nav>
    </div>
  );
};
