import React, { useState } from "react";
import { Header } from "./Header";
import { SideNav } from "./templates/SideNav";
import { Footer } from "./Footer";
import { useNavigate, useParams } from "react-router-dom";

export const JobProfile = () => {
  const [requirements, setRequirements] = useState("");
  const [description, setDescription] = useState("");
  const [title, setTitle] = useState("");
  const [image, setImage] = useState("");
  const navigate = useNavigate();
  const {id} = useParams();
  return (
    <div className="w-[100%] h-full flex flex-col overflow-x-hidden">
      <Header />
      <div className="bg-gradient-to-r from-slate-900 via-red-900 to-slate-900 w-full h-full flex ">
        <SideNav className="w-[16%]" />
        <div className="w-[84%] h-[100%]">
          <div
            className={`w-full h-full flex flex-col items-center gap-4 overflow-auto `}
            style={{ scrollbarWidth: "none" }}
          >
            <img src="https://storage.googleapis.com/gd-prod/images/f4f4bc5f-904a-4b61-972f-c403bc060679.faa49ab5e1fff880.webp" alt=""  className=" w-32 h-32 object-cover rounded-full mt-5"/>
            <div className=" text-zinc-800  p-2 bg-gradient-to-r from-rose-100 to-teal-100  -translate-y-5 text-xl font-bold rounded-2xl mt-2 ">Full-Stack Developer</div>
            <div className="text-zinc-100 h-30 text-center">Description Lorem ipsum dolor sit amet, consectetur adipisicing elit. Neque, ipsa fugit. Aliquam facilis expedita dignissimos asperiores ullam eveniet, placeat debitis, eaque rerum recusandae dolorem corrupti, nobis voluptate non sequi nemo.</div>
            <div className="text-zinc-100 h-30 text-center">Requirements Lorem ipsum dolor sit amet consectetur, adipisicing elit. Quae ullam quisquam similique fugiat quibusdam provident molestiae porro minus iure inventore omnis aspernatur incidunt velit, praesentium eum tenetur assumenda quos natus.</div>
            <button className=" bg-black rounded-md text-base font-semibold text-zinc-100 p-2">Apply</button>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};
