    cons

    const instance = axios.create({
        baseURL: "http://localhost:8080/",
        headers: {
            Accept: "application/json",
            Authorization: `Bearer ${auth.accessToken}`
        },
    });

    return instance;
};

export default MyAxiosInstance;
