import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import JobListingService from "../services/JobListingService";

export const Search = () => {
  const [query, setQuery] = useState("");
  const [searches, setSearches]= use
  const GetSearches = async () => {
    try{
        const d = await JobListingService.GetByTitleContainig(query);
        console.log("Search of job by title ", d);
    }
    catch(error){
        console.log("Error: ", error);
    }
  }
  useEffect(()=>{
    GetSearches();
  },[query])
  return (
    <div className="w-full h-[10vh] relative flex translate-x-44 items-center">
      <i className="ri-search-line text-base text-zinc-100"></i>
      <input
        type="text"
        onChange={(e) => setQuery(e.target.value)}
        value={query}
        placeholder="search jobs on the basis of title"
        className="w-[40%] mx-3 p-1 text-xl border-black rounded-md"
      />
      {query.length > 0 && (
        <i
          onClick={() => setQuery("")}
          className="ri-close-line text-2xl text-zinc-100"
        ></i>
      )}

      <div className="w-[50%] max-h-[30vh] absolute bg-red-100 top-[90%]  overflow-auto">
